import axios from 'axios';
import React, { Component } from 'react';
import './About.css'

class About extends Component {

  state = {
    users : [],
    loading : "dots-flow"
  }
  
  componentDidMount(){
    setTimeout(() => {
      axios.get("https://jsonplaceholder.typicode.com/users")
      .then((resp) => {
          this.setState({
            users : resp.data
          })        
        }
      );
    }, 4000);
  };

  loaded = () => {
    document.getElementById('about').classList.remove('dots-flow')
  }

  render(){
    const usersList = this.state.users.map(
      (user) => {
        document.getElementById('about').classList.remove('dots-flow')
        return(
          <div key={user.id} className={'user'}>
            <span>Name : {user.name}</span>
            <br></br>
            <span>Email : {user.email}</span>
          </div>
        );
      }
    );
    return(
      <div>
        <h1>&nbsp;Our team</h1>
        <div id='about' className='dots-flow'>
        {usersList}
        </div>
      </div>
    )
  }
}

export default About;
