import React from 'react';
import { useFormik, Formik, Form, Field, ErrorMessage } from "formik";
//import { useNavigate } from 'react-router-dom'
import * as Yup from 'yup'



const Forme = () => {

    /*const validate = (values) => {
        const errors = {}
    
        if(!values.name){
            errors.name = 'Required'
        } else if(!isNaN(values.name)){
            errors.name = 'Name is not a number'
        }
    
        if(!values.phone){
            errors.phone = 'Required'
        } else if( (isNaN(values.phone) || values.phone.length != 8) ){
            errors.phone = 'Phone should be 8 numbers'
        }
    
        return errors
    };

    let navigate = useNavigate()*/

    const validationSchema = Yup.object({
        name : Yup.string().required('This field is Required'),
        phone : Yup.string().required('This field is Required')
    })

    /*const verifName = () => {
        if(formik.errors.name && formik.touched.name){
            let nameTag = document.getElementById('name')
            nameTag.style.borderColor = '#084298';
            nameTag.style.boxShadow = 'rgb(248 210 248) 0px 0px 0px 0.2rem';
            return(<div style={{color : '#084298'}}>{formik.errors.name}</div>)
        } else {
            try{
                let nameTag = document.getElementById('name')
                nameTag.style.borderColor = '';
                nameTag.style.boxShadow = '';
                return(<br />)
            } catch{ return(<br />) }
        }
    }*/

    return(
        <Formik className='mx-5 mt-2'
        initialValues = {
            {
                name : '',
                phone : ''
            }
        }
        validationSchema
        onSubmit  = {
            (values) => {
                alert('Name : ' + values.name+'\nPhone : ' + values.phone+'\nNow go home')
                //navigate('/')
            }
        }
        >
            {
                formik => {
                    return(
                        <div>
                            <h1>useformik Form</h1>
                            <Form>
                                <label htmlFor='name'>Name :</label>
                                <Field type='text' id='name' className='form-control' 
                                />
                                {verifName()}
                
                                <label htmlFor='phone'>Phone :</label>
                                <Field type='text' id='phone' className='form-control'
                                />
                                {formik.errors.phone && formik.touched.phone ? <div>{formik.errors.phone}</div> : <br></br>}
                
                                <br></br>
                
                                <button type='submit'>Submit</button>
                            </Form>
                        </div>
                    )
                }
            }
        </Formik>
    )


};

export default Forme;
