import axios from 'axios';
import React, { Component } from 'react';
import './About.css'

class About extends Component {

  state = {
    users : [],
    loading : "dots-flow"
  }
  
  componentDidMount(){
    this.setState({
      loading : "dots-flow"
    })
    setTimeout(() => {
      axios.get("https://jsonplaceholder.typicode.com/users")
      .then((resp) => {
          this.setState({
            users : resp.data
          })        
        }
      );
    }, 5000)

    this.setState({
      loading : ""
    })
  };

  loaded = () => {
    console.log('loading')
  }

  render(){
    const usersList = this.state.users.map(
      (user) => {
        return(
          <div id='about' key={user.id} className={'user '+this.state.loading}>
            <span>Name : {user.name}</span>
            <br></br>
            <span>Email : {user.email}</span>
          </div>
        );
      }
    );
    return(
      <div onLoadStart={this.loaded}>
        <h1>&nbsp;Our team</h1>
        {usersList}
      </div>
    )
  }
}

export default About;
