import React from 'react';
import { useFormik } from "formik";
import { useNavigate } from 'react-router-dom'
import * as Yup from 'yup'



const Forme = () => {

    /*const validate = (values) => {
        const errors = {}
    
        if(!values.name){
            errors.name = 'Required'
        } else if(!isNaN(values.name)){
            errors.name = 'Name is not a number'
        }
    
        if(!values.phone){
            errors.phone = 'Required'
        } else if( (isNaN(values.phone) || values.phone.length != 8) ){
            errors.phone = 'Phone should be 8 numbers'
        }
    
        return errors
    };

    let navigate = useNavigate()*/

    const validationSchema = Yup.object({
        name : Yup.string().required('This field is Required'),
        phone : Yup.string().required('This field is Required')
    })

    const formik = useFormik({
        initialValues: {
            name : '',
            phone : ''
        },
        validationSchema,
        onSubmit : (values) => {
            alert('Name : ' + values.name+'\nPhone : ' + values.phone+'\nNow go home')
            //navigate('/')
        }
    });

    return(
        <div className='mx-5 mt-2'>
            <h1>useformik Form</h1>
            <form onSubmit={formik.handleSubmit}>
                <label htmlFor='name'>Name :</label>
                <input type='text' id='name' className='form-control' 
                value={formik.values.name} onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                />
                {formik.errors.name && formik.touched.name ? () =>{
                    document.getElementById('name').style.boxShadow = '0 0 0 0.2rem rgba(50, 239, 66, 0.25) !important';
                    return(<div>{formik.errors.name}</div>)
                }  : <br />}


                <label htmlFor='phone'>Phone :</label>
                <input type='text' id='phone' className='form-control'
                value={formik.values.phone} onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                />
                {formik.errors.phone && formik.touched.phone ? () => {
                    return(<div>{formik.errors.phone}</div>)
                } : <br></br>}

                <br></br>

                <button type='submit'>Submit</button>
            </form>
        </div>
    )
};

export default Forme;
