import React from 'react';
import { useFormik } from "formik";

const Form = () => {

    const validate = (values) => {
        const errors = {}
        if(!Formik.values.name){
            errors.name = 'Required'
        }

        if(!Formik.values.phone){
            errors.phone = 'Required'
        }
    };

    const Formik = useFormik({
        initialValues: {
            name : '',
            phone : ''
        },
        validate,
        onSubmit : (values) => {
            alert(values+'\nNow go home')
            window.history.pushState(null, 'home', '/')
        }
    });

    return(
        <div className='mx-5 mt-2'>
            <h1>useFormik Form</h1>
            <form onSubmit={Formik.handleSubmit}>
                <label htmlFor='name'>Name :{Formik.errors.name ? <span>{Formik.errors.name}</span> : null}</label>
                <input type='text' id='name' className='form-control' value={Formik.values.name} onChange={Formik.handleChange}/>

                <label htmlFor='phone'>Name :{Formik.errors.phone ? <span>{Formik.errors.phone}</span> : null}</label>
                <input type='text' id='phone' className='form-control' value={Formik.values.phone} onChange={Formik.handleChange}/>

                <button type='submit'>Submit</button>
            </form>
        </div>
    )
};

export default Form;
