import axios from 'axios';
import React, { Component } from 'react';
import './About.css'

class About extends Component {

  state = {
    users : [],
    loading : ""
  }
  
  componentDidMount(){
    this.setState({
      loading : "dots-flow"
    })
    axios.get("https://jsonplaceholder.typicode.com/users")
    .then((resp) => {
        this.setState({
          users : resp.data
        })        
      }
    );
    this.setState({
      loading : ""
    })
  }
  render(){
    const usersList = this.state.users.map(
      (user) => {
        return(
          <div id='about' key={user.id} className={'user '}>
            <span>Name : {user.name}</span>
            <br></br>
            <span>Email : {user.email}</span>
          </div>
        );
      }
    );
    return(
      <div className={'nn'+this.state.loading}>
        <h1>&nbsp;Our team</h1>
        {usersList}
      </div>
    )
  }
}

export default About;
