import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup'

const News = () => (
  <div>
    <h1>Any place in your app!</h1>
    <Formik
      initialValues={{ email: '', password: '' }}
      validate={values => {
        const errors = {};
        if (!values.email) {
          errors.email = 'Required';
        } else if (
          !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
        ) {
          errors.email = 'Invalid email address';
        }
        return errors;
      }}
      onSubmit={(values, { setSubmitting }) => {
        setTimeout(() => {
          alert(JSON.stringify(values, null, 2));
          setSubmitting(false);
        }, 400);
      }}
    >
      {
        ({ isSubmitting }) => (
          <Form>
            <Field type="email" name="email" />
            <ErrorMessage name="email" component="div" />
            <Field type="password" name="password" />
            <ErrorMessage name="password" component="div" />
            <button type="submit" disabled={isSubmitting}>
              Submit
            </button>
          </Form>
        )
      }
    </Formik>
  </div>
);
====================================================================================================================================================================
====================================================================================================================================================================*/

import React from 'react';
import { Form, Formik, Field, ErrorMessage } from 'formik';
import './News.css'

// A custom validation function. This must return an object
// which keys are symmetrical to our values/initialValues


const News = () => {
  // Pass the useFormik() hook initial form values, a validate function that will be called when
  // form values change or fields are blurred, and a submit function that will
  // be called when the form is submitted

  /*const formik = useFormik({
    initialValues: {
      firstName: '',
      lastName: '',
      email: 'fljkleh@fhfd.fddfh',
    },
    validate,
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
  });

  const veriflastName = () => {
    if(formik.errors.lastName && formik.touched.lastName) {
      document.getElementById('lastName').classList.add('field');
      return (<span>{' ['+formik.errors.lastName+']'}</span>)
    } else {
      try{
        document.getElementById('lastName').classList.remove('field');
        return null
      } catch{

      }
    } 
  }

  const veriffirstName = () => {
    if(formik.errors.firstName && formik.touched.firstName){
      document.getElementById('firstName').classList.add('field');
      return(<span>{' ['+formik.errors.firstName+']'}</span>)
    } else {
      try{
        document.getElementById('firstName').classList.remove('field');
        return null
      } catch{}
    } 
  } 

  const verifEmail = () => {
    if(formik.errors.email && formik.touched.email){
      document.getElementById('email').classList.add('field');
      return(<span>{' ['+formik.errors.email+']'}</span>)
    } else {
      try{
        document.getElementById('email').classList.remove('field');
        return null
      } catch{}
    } 
  } */

  return (
    <Formik
    initialValues={
      {
        firstName: '',
        lastName: '',
        email: 'fljkleh@fhfd.fddfh',
      }
    }
    validate = {
      values => {
        const errors = {};
        if (!values.firstName) {
          errors.firstName = 'Required';
          document.getElementById('firstName').classList.add('field');
        } else if (values.firstName.length > 15) {
          errors.firstName = 'Must be 15 characters or less';
          document.getElementById('firstName').classList.add('field');
        } else{
          document.getElementById('firstName').classList.remove('field');
        }
      
        if (!values.lastName) {
          errors.lastName = 'Required';
        } else if (values.lastName.length > 20) {
          errors.lastName = 'Must be 20 characters or less';
        }
      
        if (!values.email) {
          errors.email = 'Required';
        } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
          errors.email = 'Invalid email address';
        }
        console.log(errors)
        return errors;
      }
    }
    onSubmit = {
      values => {
        alert(JSON.stringify(values, null, 2));
      }
    }
    >      
      
      <Form className='mx-5 mt-2'>
        <h1>useFormik Form</h1>
        <label htmlFor="firstName">First Name</label>
        <span className="text-danger">
          <ErrorMessage name="firstName" />
        </span>
        <br />
        <Field
          className="form-control w-50"
          id="firstName"
          name="firstName"
          type="text"
        />

        <br />

        <label htmlFor="lastName">Last Name</label>
        <span className="text-danger">
          <ErrorMessage name="lastName" />
        </span>
        <br />
        <Field
          className="form-control w-50"
          id="lastName"
          name="lastName"
          type="text"
        />


        <br></br>

        <label htmlFor="email">Email Address</label>
        <span className="text-danger">
        <ErrorMessage name="email" />
        </span>
        <br />
        <Field
          className="form-control w-50"
          id="email"
          name="email"
          type="email"
        />

        <br></br>
        <button type="submit" className='btn btn-primary'>Submit</button>
      </Form>
    </Formik>
  );
};

export default News;
