import React from 'react';
import { useFormik } from 'formik';
import './News.css'

// A custom validation function. This must return an object
// which keys are symmetrical to our values/initialValues
const validate = values => {
  const errors = {};
  if (!values.firstName) {
    errors.firstName = 'Required';
  } else if (values.firstName.length > 15) {
    errors.firstName = 'Must be 15 characters or less';
  }

  if (!values.lastName) {
    errors.lastName = 'Required';
  } else if (values.lastName.length > 20) {
    errors.lastName = 'Must be 20 characters or less';
  }

  if (!values.email) {
    errors.email = 'Required';
  } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
    errors.email = 'Invalid email address';
  }

  return errors;
};

const News = () => {
  // Pass the useFormik() hook initial form values, a validate function that will be called when
  // form values change or fields are blurred, and a submit function that will
  // be called when the form is submitted

  const formik = useFormik({
    initialValues: {
      firstName: '',
      lastName: '',
      email: '',
    },
    validate,
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
  });

  const verif = () => {
    if(formik.errors.lastName) {
      window.alert('lklj')
      document.getElementById('lastName').classList.add('border-danger');
      return (<span>{' ['+formik.errors.lastName+']'}</span>)
    } else{
      window.alert('no')
    }
  }

  return (
    <div className='mx-5 mt-2'>      
    <h1>useFormik Form</h1>
    <form onSubmit={formik.handleSubmit}>
      <label htmlFor="firstName">First Name</label>
      <span className="text-danger">
        {formik.errors.firstName ? <span>{' ['+formik.errors.firstName+']'}</span> : null}
      </span>
      <br />
      <input
        className="form-control w-50"
        id="firstName"
        name="firstName"
        type="text"
        onChange={formik.handleChange}
        value={formik.values.firstName}
      />

      <br />

      <label htmlFor="lastName">Last Name</label>
      <span className="text-danger">
      {formik.errors.lastName ? verif() : null}

        
      </span>
      <br />
      <input
        className="form-control w-50"
        id="lastName"
        name="lastName"
        type="text"
        onChange={formik.handleChange}
        value={formik.values.lastName}
      />


      <br></br>

      <label htmlFor="email">Email Address</label>
      <span className="text-danger">
        {formik.errors.email ? <span>{' ['+formik.errors.email+']'}</span> : null}
      </span>
      <br />
      <input
        className="form-control w-50"
        id="email"
        name="email"
        type="email"
        onChange={formik.handleChange}
        value={formik.values.email}
      />

      <br></br>
      <button type="submit" className='btn btn-primary'>Submit</button>
    </form>
    </div>
  );
};

export default News;
