import React, { Component } from 'react';

class Home extends Component {

    state = {
        time : new Date
    }

    componentDidMount(){
        setInterval( () => {
            this.setState({
              time : new Date
            })
          }, 100 
        )
    }

    render () {
        return(
            <div>
                <h1>Homepage</h1>
                <h3>Clock : {this.state.time.toLocaleTimeString()}</h3>
            </div>
        )
    }
}
export default Home
