import React from 'react';
import { useFormik } from "formik";
import { useNavigate } from 'react-router-dom'

const validate = (values) => {
    const errors = {}
    if(!values.name){
        errors.name = 'Required'
    } else if(!isNaN(values.name)){
        errors.name = 'Name is not a number'
    }

    if(!values.phone){
        errors.phone = 'Required'
    } else if(isNaN(values.phone) || values.phone.length != 8){
        errors.phone = 'Phone should be 8 numbers'
    }

    return errors
};

const Form = () => {

    let navigate = useNavigate()

    const Formik = useFormik({
        initialValues: {
            name : '',
            phone : ''
        },
        validate,
        onSubmit : (values) => {
            alert('Name : ' + values.name+'\nPhone : ' + values.phone+'\nNow go home')
            navigate('/')
        }
    });

    return(
        <div className='mx-5 mt-2'>
            <h1>useFormik Form</h1>
            <form onSubmit={Formik.handleSubmit}>
                <label htmlFor='name'>Name :</label>
                <input type='text' id='name' className='form-control' 
                value={Formik.values.name} onChange={Formik.handleChange}
                onBlur={Formik.handleBlur}
                />
                {Formik.errors.name ? <div>{Formik.errors.name}</div> : <br />}


                <label htmlFor='phone'>Phone :</label>
                <input type='text' id='phone' className='form-control'
                value={Formik.values.phone} onChange={Formik.handleChange}
                onBlur={Formik.handleBlur}
                />
                {Formik.errors.phone ? <div>{Formik.errors.phone}</div> : <br></br>}

                <br></br>

                <button type='submit'>Submit</button>
            </form>
        </div>
    )
};

export default Form;
