import React from 'react';
import { useFormik } from "formik";
import { useNavigate } from 'react-router-dom'

const validate = (values) => {
    const errors = {}

    if(!values.name && formik.touched.name){
        errors.name = 'Required'
    } else if(!isNaN(values.name) && formik.touched.name){
        errors.name = 'Name is not a number'
    }

    if(!values.phone && formik.touched.phone){
        errors.phone = 'Required'
    } else if( (isNaN(values.phone) || values.phone.length != 8) && formik.touched.phone ){
        errors.phone = 'Phone should be 8 numbers'
    }

    return errors
};

const Forme = () => {

    let navigate = useNavigate()

    const formik = useFormik({
        initialValues: {
            name : '',
            phone : ''
        },
        validate,
        onSubmit : (values) => {
            alert('Name : ' + values.name+'\nPhone : ' + values.phone+'\nNow go home')
            navigate('/')
        }
    });

    return(
        <div className='mx-5 mt-2'>
            <h1>useformik Form</h1>
            <form onSubmit={formik.handleSubmit}>
                <label htmlFor='name'>Name :</label>
                <input type='text' id='name' className='form-control' 
                value={formik.values.name} onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                />
                {formik.errors.name ? <div>{formik.errors.name}</div> : <br />}


                <label htmlFor='phone'>Phone :</label>
                <input type='text' id='phone' className='form-control'
                value={formik.values.phone} onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                />
                {formik.errors.phone ? <div>{formik.errors.phone}</div> : <br></br>}

                <br></br>

                <button type='submit'>Submit</button>
            </form>
        </div>
    )
};

export default Forme;
