import React, {Component} from "react";

class Contact extends Component {

    state = {
        newNum : '',
        contacts : [
            {num : '+216 1684984'}
        ]
    }

    newNumUpdate = (e)=>{
        this.setState({
            ...this.state.contacts,
            newNum : e.target.value
        })
    }

    addNum = ()=>{
        let contacts = [...this.state.contacts]
        contacts.push({ num : '+216 '+this.state.newNum })
        this.setState({
            contacts,
            newNum :''
        })
    }

    render (){
        return (
            <div className="mx-5 my-3">
                <div className="row g-0">
                    <input className="col-auto form-control w-50 me-3" onChange={this.newNumUpdate} /> 
                    <button className="col-auto btn btn-success" onClick={this.addNum}>+ ADD</button>
                </div>
                <br />
                <h1 className="p-0">Contacts :</h1>
                <ul>
                    {
                        this.state.contacts.map(
                            (contact) =>{
                                return(<li key={Math.random()}>{contact.num}</li>)
                            } 
                        )
                    }
                </ul>
            </div>
        );
    }
}

export default Contact;
