/* Render Prop
import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup'

const News = () => (
  <div>
    <h1>Any place in your app!</h1>
    <Formik
      initialValues={{ email: '', password: '' }}
      validate={values => {
        const errors = {};
        if (!values.email) {
          errors.email = 'Required';
        } else if (
          !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
        ) {
          errors.email = 'Invalid email address';
        }
        return errors;
      }}
      onSubmit={(values, { setSubmitting }) => {
        setTimeout(() => {
          alert(JSON.stringify(values, null, 2));
          setSubmitting(false);
        }, 400);
      }}
    >
      {
        ({ isSubmitting }) => (
          <Form>
            <Field type="email" name="email" />
            <ErrorMessage name="email" component="div" />
            <Field type="password" name="password" />
            <ErrorMessage name="password" component="div" />
            <button type="submit" disabled={isSubmitting}>
              Submit
            </button>
          </Form>
        )
      }
    </Formik>
  </div>
);
====================================================================================================================================================================
====================================================================================================================================================================*/

import React from 'react';
import { Form, Formik, Field, ErrorMessage, FieldArray } from 'formik';
import './News.css'

// A custom validation function. This must return an object
// which keys are symmetrical to our values/initialValues


const News = () => {
  return (
    <Formik
    initialValues={
      {
        firstName: '',
        lastName: '',
        email: 'fljkleh@fhfd.fddfh',
        description : '',
        phNumbers : ['']
      }
    }
    validate = {
      (values, touched) => {
        const errors = {};
        const firstNameElement = document.getElementById('firstName');
        const lastNameElement = document.getElementById('lastName');
        const emailElement = document.getElementById('email');
        const descriptionElement = document.getElementById('description');

        if (!values.firstName) {
          errors.firstName = 'Required';
          firstNameElement.classList.add('field');
        } else if ((values.firstName.length > 15)) {
          errors.firstName = 'Must be 15 characters or less';
          firstNameElement.classList.add('field');
        } else{
          firstNameElement.classList.remove('field');
        }
      
        if (!values.lastName) {
          errors.lastName = 'Required';
          lastNameElement.classList.add('field');
        } else if (values.lastName.length > 20) {
          errors.lastName = 'Must be 20 characters or less';
          lastNameElement.classList.add('field');
        } else{
          lastNameElement.classList.remove('field');
        }
      
        if (!values.email) {
          errors.email = 'Required';
          emailElement.classList.add('field');
        } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
          errors.email = 'Invalid email address';
          emailElement.classList.add('field');
        } else{
          emailElement.classList.remove('field');
        }

        if (!values.description) {
          errors.description = 'Required';
          descriptionElement.classList.add('field');
        } else{
          descriptionElement.classList.remove('field');
        }
        console.log(touched)
        return errors;
      }
    }
    validateOnBlur={false}
    onSubmit = {
      values => {
        alert(JSON.stringify(values, null, 2));
      }
    }
    >     
    {
      formik =>{
        return(
          <Form className='mx-5 mt-2'>
          <h1>useFormik Form</h1>
          <div className=''>
            <label htmlFor="firstName">First Name</label>
            <span className="text-danger ms-2">
              <ErrorMessage name="firstName" />
            </span>
            <Field className="form-control w-50" id="firstName" name="firstName" type="text" />
          </div>
  
          <br></br>
  
          <div>
            <label htmlFor="lastName">Last Name</label>
            <span className="text-danger ms-2">
              <ErrorMessage name="lastName" />
            </span>
            <Field className="form-control w-50" id="lastName" name="lastName" type="text" />
          </div>
  
          <br></br>
  
          <div>
            <label htmlFor="email">Email Address</label>
            <span className="text-danger ms-2">
              <ErrorMessage name="email" />
            </span>
            <br />
            <Field className="form-control w-50" id="email" name="email" type="email" />
          </div>
  
          <br></br>
  
          <div>
            <label htmlFor="description">Description</label>
            <span className="text-danger ms-2">
              <ErrorMessage name="description" />
            </span>
            <br />
            <Field as='textarea' className="form-control w-50" id="description" name="description" placeholder="What do you think ?" />
          </div>
  
          <br></br>
  
          <div>
            <label htmlFor="phNumbers">Phone Numbers</label>
            <span className="text-danger ms-2">
              <ErrorMessage name="phNumbers" />
            </span>
            <br />
            <FieldArray id="phNumbers" name="phNumbers" >
            {
              (fieldArrayProps) => {
                //console.log('Field Array Props : ', fieldArrayProps)
                const {push, remove, form} = fieldArrayProps
                const {values} = form
                const {phNumbers} = values
                //console.log('Numbers : ', phNumbers)
                return(
                  <div>
                    {
                      phNumbers.map((phnumber, index) => {
                        return(  
                          <div key={index}>
                            <Field name={`phNumbers[${index}]`} />
                            { 
                              index > 0 ? <button type='button' onClick={() => {remove(index)}}> - </button> : null
                            }
                            <button type='button' onClick={() => {push('')}}> + </button>
                          </div>
                        )
                      })
                    }
                  </div>
                )
              }
            }
            </FieldArray>
          </div>
  
          <br></br>
  
          <button type="submit" className='btn btn-primary'>Submit</button>
        </Form>
        )
      }
    }

    </Formik>
  );
};

export default News;
