import React, { Component } from 'react';
import './App.css';
import Nav from "./components/Nav";
import Title1 from './components/comp1'
import Title2 from './components/comp2'
import { BrowserRouter, Route, Routes } from 'react-router-dom';

class App extends Component {
  state = { 
    opinion : "Recommended",
    title : "Hello"
  } 

  changeTitle = (e) => {
    this.setState({
      title : e.target.value
    })      
  }

  changeColor = () => {
    var o = Math.round, r = Math.random, s = 255;
    document.getElementById('title').style.backgroundColor = 'rgba(' + o(r()*s) + ',' + o(r()*s) + ',' + o(r()*s) + ',' + r().toFixed(1) + ')';
  }

  render() { 
    return (
      <BrowserRouter>
        <Routes>

          <Route path='/' element={<Title1 />}></Route>
          <Route path='/title2' element={<Title2 />}></Route>
        </Routes>
      </BrowserRouter>
    );
  }
}

export default App;
