import React from 'react';
import { useFormik } from "formik";
import { useNavigate } from 'react-router-dom'


const Form = () => {

    let navigate = useNavigate()

    const validate = (values) => {
        const errors = {}
        if(!Formik.values.name){
            errors.name = 'Required'
        }

        if(!Formik.values.phone){
            errors.phone = 'Required'
        }
    };

    const Formik = useFormik({
        initialValues: {
            name : '',
            phone : ''
        },
        validate,
        onSubmit : (values) => {
            alert('Name : ' + values.name+'Phone : ' + values.name+'\nNow go home')
            navigate('/')
        }
    });

    return(
        <div className='mx-5 mt-2'>
            <h1>useFormik Form</h1>
            <form onSubmit={Formik.handleSubmit}>
                <label htmlFor='name'>Name :{Formik.errors.name ? <span>{Formik.errors.name}</span> : null}</label>
                <input type='text' id='name' className='form-control' value={Formik.values.name} onChange={Formik.handleChange}/>

                <br></br>

                <label htmlFor='phone'>Phone :{Formik.errors.phone ? <span>{Formik.errors.phone}</span> : null}</label>
                <input type='text' id='phone' className='form-control' value={Formik.values.phone} onChange={Formik.handleChange}/>

                <br></br>

                <button type='submit'>Submit</button>
            </form>
        </div>
    )
};

export default Form;
