    title : "Hello"
  } 
  render() { 
    return (
      <div className="App">
        <center>
        <table>
          <tr>
            <td>{this.state.title}</td>
          </tr>
          <tr>
            <Title1 opinion={this.state.opinion} />
          </tr>
          <tr>
            <Title2 />
          </tr>
          <tr>
            <td><input type="text" onChange={changeTitle}/></td>
            <td><button onClick={this.changeColor}>Change Title color</button></td>
          </tr>
        </table>
        </center>
      </div>
    );
  }
}

export default App;
