import React, { Component } from 'react';
import './App.css';
import Title1 from './comp1'
import Title2 from './comp2'

class App extends Component {
  state = { 
    opinion : "Recommended",
    title : "Hello"
  } 

  changeTitle = (e) => {
    this.setState({
      title : e.target.value
    })      
  }

  changeColor = () => {

  }

  render() { 
    return (
      <div className="App">
        <center>
        <table>
          <tr>
            <td><h1 id='title'>{this.state.title}</h1></td>
          </tr>
          <tr>
            <Title1 opinion={this.state.opinion} />
          </tr>
          <tr>
            <Title2 />
          </tr>
          <tr>
            <td><input type="text" onChange={this.changeTitle}/></td>
            <td><button onClick={this.changeColor}>Change Title color</button></td>
          </tr>
        </table>
        </center>
      </div>
    );
  }
}

export default App;
